import React from 'react';
import PropTypes from 'prop-types';

const CartItem = ({ name, price, currency, onClick, qtd }) => {

    return (
        <div className="cart-item">
            <div>
                <button className="btn btn-danger btn-xs" onClick={onClick}>X</button>
                <span className="cart-item__name">{name}</span>
                <button className="btn btn-success btn-xs">-</button>
                <span className="cart-item__name">{qtd}</span>
                <button className="btn btn-success btn-xs"  onChange={e => setQtd()}>+</button>
            </div>
            <div className="cart-item__price">{currency}{price}</div>
        </div>
    );
}

CartItem.propTypes = {
    name: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    currency: PropTypes.string.isRequired,
    onClick: PropTypes.func.isRequired,
}

export default CartItem;
