import React from 'react';
import PropTypes from 'prop-types';

const CartItem = ({ name, price, currency, qtd, onClick }) => {
    qtd = 1;    

    return (
        <div className="cart-item">
            <div>
                <button className="btn btn-danger btn-xs" onClick={remove}>X</button>
                <span className="cart-item__name">{name}</span>
                <span className="cart-item__name">{qtd} <button className="btn btn-danger btn-xs" onClick={onClick}>+</button></span>
            </div>
            <div className="cart-item__price">{currency}{price}</div>
        </div>
    );
}

CartItem.propTypes = {
    name: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    qtd: PropTypes.number.isRequired,
    currency: PropTypes.string.isRequired,
    remove: PropTypes.func.isRequired,
    add: PropTypes.func.isRequired
}


export default CartItem;
