import React from 'react';
import PropTypes from 'prop-types';

const CartItem = ({ name, price, total, currency, qtd, onClick, changeQuantity}) => {

    return (
        <div className="cart-item">
            <div>
                <button className="btn btn-danger btn-xs" onClick={onClick}>X</button>
                <span className="cart-item__name">{name}</span>
                <button className="btn btn-success btn-xs" onClick={changeQuantity("menus")}>-</button>
                <span className="cart-item__name">{qtd}</span>
                <button className="btn btn-success btn-xs" onClick={changeQuantity("more")}>+</button>
            </div>
            <div className="cart-item__price">{currency}{total}</div>
        </div>
    );
}

CartItem.propTypes = {
    name: PropTypes.string.isRequired,
    price: PropTypes.number.isRequired,
    total: PropTypes.number.isRequired,
    currency: PropTypes.string.isRequired,
    qtd: PropTypes.number.isRequired,
    onClick: PropTypes.func.isRequired,
    changeQuantity: PropTypes.func.isRequired,
}

export default CartItem;
